    }

]
 function renderArticleData(){
     const ArticleDataRendered=ArticleData.map(
         (part)=>
         <div key={part.id}>
            <h1>{part.title}</h1>
            <p>{part.comment}</p>
        </div>
     )
     return ArticleDataRendered
 }

const Article=()=>{

    return(
        <article class="article margin-v-45">
          {  renderArticleData()}

        </article>

    )
}
