function submit(){
    const objectForm=React.getElementsByTagName("input")[0].value;
    console.log(objectForm);
}
class DownloadAppForm extends React.Component{
    constructor(){
        super();
        this.submit=this.submit.bind(this);
    }
     submit=(e)=>{
        const objectForm=React.getElementsByTagName("input")[0].value;
        console.log(objectForm);
        e.preventDefault();
    }
   render(){
    return(
        <form class="app-flex-form" onSubmit={this.submit}>
            <input  type="text" placeholder="مثلا **91234509"  required/>
            <button   type="submit">دریافت لینک از طریق sms</button>
            <p class="app-comment-2">اپلیکیشن ریحون برای Android و iOS در دسترس است.</p>
        </form>
    )
   }
}
export default DownloadAppForm;
