const FooterTopData=[
    {
        title:["پشتیبانی ریحون","سوالات متداول","تماس با پشیبانی","قوانین و مقررات"]

    },
    {
        title:["رستوران ها","ثبت رستوران"]
    },
    {
        title:["تماس با ریحون","درباره ریحون","تماس با ما","وبلاگ ریحون"]
    },
    {
        title:["اپلیکیشن های موبایل"]
    },
    {image:[
        "https://dist.reyhoon-static.com/web-assets/images/app-links/google-play-dark.png?x-oss-process=image/resize,w_180"
        ,"https://dist.reyhoon-static.com/web-assets/images/app-links/sib-app-dark.png?x-oss-process=image/resize,w_180"
        ,"https://dist.reyhoon-static.com/web-assets/images/app-links/cafebazaar-dark.png?x-oss-process=image/resize,w_180"
    ]}
]


const  renderFooter=()=>{
 const   FooterTopRendered=FooterTopData.map(
        (card)=> (card.title)?
        <ul>{card.title.map((item)=><li>{item}</li>)}</ul>:
        <ul>{card.image.map((item)=><li><a><img src={item}/></a></li>)}</ul>
    )
       
    return(
        FooterTopRendered
    );
}
const FooterTop=()=>{
return(
    <div class="footer-top">
            {renderFooter()},
            <p> 
                  مراقبت و محافظت از حساب کاربری و رمزعبور 
                هر کاربر بر عهده کاربر است. ریحون سریعترین راه
                 سفارش آنلاین غذا است. منوی عکس‌دار رستوران‌های 
                اطرافتان را بر اساس مکان خود به راحتی مشاهده کنید و سفارش دهید.
            </p>
</div>
)

}
export default FooterTop
